mapper.select:
SELECT s.uuid, s.store_name, s.store_type, s.company_address_city, s.company_address_province, s.on_time_arr, s.check_sts, su.user_name, su.mobile, s.created_time, s.score, (select ifnull(convert(AVG(score_star),DECIMAL(10,2)),0.0) as avgScore from score_info where sts = 0 and relation_uuid = s.uuid) as commentScore, (SELECT COUNT(1) FROM comment_info WHERE sts = 0 AND check_sts = 1 AND relation_uuid = s.uuid) as commentNum, ( SELECT lable_desc FROM sys_dict WHERE uuid = s.store_type ) AS storeTypeName, ( SELECT area_name FROM sys_area WHERE uuid = s.company_address_province ) AS addressProvinceName, ( SELECT area_name FROM sys_area WHERE uuid = s.company_address_city ) AS addressCityName, (select GROUP_CONCAT(gp.group_name) from goods g left join goods_parent gp on g.parent_type = gp.uuid where store_uuid = s.uuid) as parentTypeName FROM (select DISTINCT st.uuid from store st left join store_brand sb on st.uuid = sb.store_uuid where st.sts = 0 and sb.sts = 0
and sb.uuid = #{param.brandUuid}
) as temp LEFT JOIN store s ON temp.uuid = s.uuid LEFT JOIN store_user su ON s.uuid = su.store_uuid and su.person_type = '501' and su.sts = 0 WHERE s.sts = 0
AND s.store_name LIKE CONCAT('%',#{param.storeName},'%')
AND s.store_type in
#{item}
AND s.store_type = #{param.storeType}
AND su.user_name LIKE CONCAT('%',#{param.contactsName},'%')
AND su.mobile LIKE CONCAT('%',#{param.mobile},'%')
AND s.company_address_county = #{param.areaUuid}
AND s.check_sts = #{checkSts}
AND s.uuid in ( SELECT g.`store_uuid` FROM `goods` g WHERE g.sts = 0 AND g.`parent_type` = #{param.levelOne} )
AND s.uuid in ( SELECT g.`store_uuid` FROM `goods` g WHERE g.sts = 0 AND g.`sub_type` = #{param.levelTwo} )
order by s.created_time desc
