mapper.select:
SELECT count( 1 ) AS registerCount, IFNULL( sum( order_count ), 0 ) AS orderCount, IFNULL( sum( case_count ), 0 ) AS caseCount, IFNULL( sum( qa_count ), 0 ) AS qaCount, IFNULL( sum( support_count ), 0 ) AS supportCount FROM technician WHERE sts = 0
user_name LIKE CONCAT('%',#{userName},'%')
mobile LIKE CONCAT('%',#{mobile},'%')
address_province = #{provinceUuid}
address_city = #{cityUuid}

SELECT tech2.uuid, tech2.user_name AS userName, tech2.mobile, tech2.address_province AS addressProvince, ( SELECT area_name FROM sys_area WHERE uuid = tech2.address_province ) AS addressProvinceName, tech2.address_city AS addressCity, ( SELECT area_name FROM sys_area WHERE uuid = tech2.address_city ) AS addressCityName, tech2.address_detail AS addressDetail, tech2.technology_type AS technologyType, ( SELECT lable_desc FROM sys_dict WHERE uuid = tech2.technology_type ) AS technologyTypeName, tech2.order_count AS orderCount, tech2.qa_count AS qaCount, tech2.support_count AS supportCount, tech2.case_count AS caseCount, tech2.score, tech2.check_sts AS checkSts, tech1.technicianBrandName, tech2.working_year, tech2.created_time as createdTime, ( SELECT lable_desc FROM sys_dict WHERE uuid = tech2.state_verification ) AS stateVerification, ( SELECT lable_desc FROM sys_dict WHERE uuid = tech2.host_authentication ) AS hostAuthentication FROM ( SELECT tech.uuid, GROUP_CONCAT( ( SELECT config_name FROM vehicle_config WHERE uuid = tb.brand_uuid ) SEPARATOR '/' ) AS technicianBrandName FROM technician tech LEFT JOIN technician_brand tb ON tech.uuid = tb.technician_uuid WHERE tech.sts = 0 GROUP BY tech.uuid ) tech1 LEFT JOIN technician tech2 ON tech1.uuid = tech2.uuid WHERE tech2.sts = 0
and tech2.user_name LIKE CONCAT('%',#{userName},'%')
and tech2.mobile LIKE CONCAT('%',#{mobile},'%')
and tech2.address_province = #{provinceUuid}
and tech2.address_city = #{cityUuid}
and tech2.technology_type = #{technologyType}
ORDER BY tech2.check_sts ASC, tech2.created_time DESC

SELECT s.* FROM technician s WHERE s.`sts` = 0 and check_sts = 1 and cyb_auth = 1
and technology_type = #{technologyTypeUuid}
ORDER BY IFNULL(s.answer_sort_weight, 1000000) asc,IFNULL(s.qa_count, 0) desc,created_time desc

        SELECT * FROM technician t

            t.sts=0 AND lat_lng_distance(#{latitude},#{longitude},t.address_latitude,t.address_longitude)  <=  #{zoomNum}

            AND EXISTS
				(SELECT 1  FROM technician_brand b WHERE b.brand_uuid=#{brandUuid} and b.sts=0 and b.technician_uuid=t.uuid)


            	and technology_type = #{technologyType}
